import "./styles.css";
// import the custom hook to use in this document
import useFetch from "./useFetch";

export default function App() {
  const url = "https://v2.jokeapi.dev/joke/Programming?type=single";
  // Use the custom hook here
  const { data, loading, error, getJoke } = useFetch(url);

  // Handle the New Joke button click
  const handleNewJoke = () => {
    getJoke();
  };

  return (
    <div className="App">
      <h1>Joke Generator</h1>

      {/* Use completely different text for each state */}
      {loading && (
        <div className="status-container">
          <p className="loading-message">Please wait while we fetch a joke...</p>
        </div>
      )}

      {error && (
        <div className="status-container">
          <p className="error-message">We encountered a problem fetching your joke</p>
        </div>
      )}

      {data && !loading && !error && (
        <div className="joke-container">
          <h2 className="joke-text">{data.joke}</h2>
        </div>
      )}

      <button 
        className="btn"
        onClick={handleNewJoke}
      >
        New Joke
      </button>
    </div>
  );
}
