import { useState, useEffect } from 'react';

// Custom hook for fetching jokes
const useFetch = (url) => {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true); // Start with loading true
  const [error, setError] = useState(null);

  const getJoke = async () => {
    try {
      // Clear previous states
      setLoading(true);
      setError(null);
      setData(null); // Clear previous data
      
      const response = await fetch(url);
      
      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }
      
      const jokeData = await response.json();
      setData(jokeData);
    } catch (err) {
      setError(err.message || 'Something went wrong');
      // Ensure data is null when there's an error
      setData(null);
    } finally {
      setLoading(false);
    }
  };

  // Fetch a joke when the component mounts
  useEffect(() => {
    getJoke();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [url]); // Dependency array includes url

  return { data, loading, error, getJoke };
};

// Export the useFetch hook as a default export
export default useFetch;
