import "./styles.css";
// import the custom hook to use in this document
import useFetch from "./useFetch";

export default function App() {
  const url = "https://v2.jokeapi.dev/joke/Programming?type=single";
  // Use the custom hook here
  const { data, loading, error, getJoke } = useFetch(url);

  // Handle the New Joke button click
  const handleNewJoke = () => {
    getJoke();
  };

  // Determine which content to show based on state
  let content;
  if (loading) {
    content = <div className="loading-indicator">Loading...</div>;
  } else if (error) {
    content = <div className="error-message">Something went wrong...</div>;
  } else if (data) {
    content = <div className="joke-display">{data.joke}</div>;
  } else {
    content = <div className="empty-state">No joke loaded yet</div>;
  }

  return (
    <div className="App">
      <header>
        <h1 className="app-title">Joke Generator</h1>
      </header>

      <main className="content-area">
        {content}
      </main>

      <footer>
        <button 
          className="btn"
          onClick={handleNewJoke}
        >
          New Joke
        </button>
      </footer>
    </div>
  );
}
