import "./styles.css";
// import the custom hook to use in this document
import useFetch from "./useFetch";

export default function App() {
  const url = "https://v2.jokeapi.dev/joke/Programming?type=single";
  // Use the custom hook here
  const { data, loading, error, getJoke } = useFetch(url);

  // Handle the New Joke button click
  const handleNewJoke = () => {
    getJoke();
  };

  return (
    <div className="App" data-testid="joke-app">
      <h1 data-testid="app-heading">Joke Generator</h1>

      {/* Conditionally render content based on state */}
      {loading && <p data-testid="loading-message">Loading...</p>}
      {error && <p data-testid="error-message">Something went wrong...</p>}
      {data && !loading && !error && <h2 data-testid="joke-content">{data.joke}</h2>}

      <button 
        className="btn" 
        data-testid="new-joke-button"
        onClick={handleNewJoke}
      >
        New Joke
      </button>
    </div>
  );
}
