import "./styles.css";
// import the custom hook to use in this document
import useFetch from "./useFetch";

export default function App() {
  const url = "https://v2.jokeapi.dev/joke/Programming?type=single";
  // Use the custom hook here
  const { data, loading, error, getJoke } = useFetch(url);

  // Handle the New Joke button click
  const handleNewJoke = () => {
    getJoke();
  };

  return (
    <div className="App" role="main">
      <h1 data-testid="app-title" aria-label="application title">Joke Generator</h1>
      {/* Display loading text here */}
      {loading && <p data-testid="loading-text" aria-live="polite" role="status">Loading joke...</p>}

      {/* Display something went wrong here */}
      {error && <p data-testid="error-text" aria-live="assertive" role="alert">Error: Something went wrong while fetching joke</p>}

      {/* Do not modify the below code */}
      {data && !loading && !error && <h2 data-testid="joke-text" aria-live="polite">{data.joke}</h2>}
      <button className="btn" data-testid="new-joke-button" aria-label="Get a new joke" onClick={handleNewJoke}>Get New Joke</button>
    </div>
  );
}
