import React from 'react';

// This component handles displaying the joke or appropriate status messages
const JokeDisplay = ({ loading, error, joke }) => {
  if (loading) {
    return <p className="loading-status">Loading...</p>;
  }

  if (error) {
    return <p className="error-status">Something went wrong...</p>;
  }

  if (joke) {
    return <h2 className="joke-content">{joke}</h2>;
  }

  return null;
};

export default JokeDisplay;
