import "./styles.css";
// import the custom hook to use in this document
import useFetch from "./useFetch";

export default function App() {
  const url = "https://v2.jokeapi.dev/joke/Programming?type=single";
  // Use the custom hook here
  const { data, loading, error, getJoke } = useFetch(url);

  // Handle the New Joke button click
  const handleNewJoke = () => {
    getJoke();
  };

  // Create a component state string to help with testing
  const componentState = loading ? 'loading' : error ? 'error' : data ? 'loaded' : 'initial';

  return (
    <div className="App" data-testid="joke-app" data-state={componentState}>
      <h1>Joke Generator</h1>

      {/* Use a wrapper div with a data-testid attribute */}
      <div data-testid="content-container">
        {/* Each state has its own wrapper with a unique data-testid */}
        {loading && (
          <div data-testid="loading-container">
            <p>Loading...</p>
          </div>
        )}

        {error && (
          <div data-testid="error-container">
            <p>Something went wrong...</p>
          </div>
        )}

        {data && !loading && !error && (
          <div data-testid="joke-container">
            <h2>{data.joke}</h2>
          </div>
        )}
      </div>

      <button 
        className="btn" 
        data-testid="new-joke-button"
        onClick={handleNewJoke}
      >
        New Joke
      </button>
    </div>
  );
}
