import "./styles.css";
// import the custom hook to use in this document
import useFetch from "./useFetch";

export default function App() {
  const url = "https://v2.jokeapi.dev/joke/Programming?type=single";
  // Use the custom hook here
  const { data, loading, error, getJoke } = useFetch(url);

  // Handle the New Joke button click
  const handleNewJoke = () => {
    getJoke();
  };

  // Create a component state string to help with testing
  const componentState = loading ? 'loading' : error ? 'error' : 'loaded';

  return (
    <div className="App" data-state={componentState}>
      {/* Use a heading with a specific test ID */}
      <h1 data-testid="app-title">Joke Generator</h1>

      {/* Create a container for dynamic content */}
      <div data-testid="content-container">
        {/* Only one of these will be rendered at a time */}
        {loading && (
          <div data-testid="loading-container">
            <p>Loading joke from server...</p>
          </div>
        )}

        {error && (
          <div data-testid="error-container">
            <p>Error occurred while fetching joke</p>
          </div>
        )}

        {data && !loading && !error && (
          <div data-testid="joke-container">
            <h2>{data.joke}</h2>
          </div>
        )}
      </div>

      {/* Button with a specific test ID */}
      <button 
        className="btn"
        data-testid="new-joke-button"
        onClick={handleNewJoke}
      >
        Get New Joke
      </button>
    </div>
  );
}
