import "./styles.css";
// import the custom hook to use in this document
import useFetch from "./useFetch";

export default function App() {
  const url = "https://v2.jokeapi.dev/joke/Programming?type=single";
  // Use the custom hook here
  const { data, loading, error, getJoke } = useFetch(url);

  // Handle the New Joke button click
  const handleNewJoke = () => {
    getJoke();
  };

  return (
    <div className="App">
      {/* Use a unique ID for the title */}
      <h1 id="joke-generator-title">Joke Generator</h1>

      {/* Use IDs instead of data-testid for more specificity */}
      {loading && <p id="loading-message">Loading...</p>}

      {error && <p id="error-message">Something went wrong...</p>}

      {data && !loading && !error && <h2 id="joke-content">{data.joke}</h2>}

      {/* Use a unique ID for the button */}
      <button 
        className="btn" 
        id="new-joke-button"
        onClick={handleNewJoke}
      >
        New Joke
      </button>
    </div>
  );
}
