import "./styles.css";
// import the custom hook to use in this document
import useFetch from "./useFetch";

export default function App() {
  const url = "https://v2.jokeapi.dev/joke/Programming?type=single";
  // Use the custom hook here
  const { data, loading, error, getJoke } = useFetch(url);

  // Handle the New Joke button click
  const handleNewJoke = () => {
    getJoke();
  };

  return (
    <div className="App">
      <h1>Joke Generator</h1>

      {/* Use aria-label for better testing */}
      {loading && (
        <div aria-label="loading-state" role="status">
          <p aria-label="loading-message">Loading...</p>
        </div>
      )}

      {error && (
        <div aria-label="error-state" role="alert">
          <p aria-label="error-message">Error: Unable to load joke</p>
        </div>
      )}

      {data && !loading && !error && (
        <div aria-label="joke-display" role="region">
          <h2 aria-label="joke-content">{data.joke}</h2>
        </div>
      )}

      <button 
        className="btn"
        aria-label="fetch-new-joke"
        onClick={handleNewJoke}
      >
        New Joke
      </button>
    </div>
  );
}
